        blessed: 0,
        clap: 0,
        love: 0,
        superlike: 0,
        cool: 0,
        profileA: 0,
      },
      showArrow: false,
    }
  },
  mounted() {
    this.fetchReactions()
    const vm = this
    setTimeout(function () {
      vm.showArrow = true
    }, 1000)
  },
  methods: {
    async updateReaction(name) {
      const ref = fireDb.collection('reactions').doc('gandhi')
      const document = {
        [name]: this.reactions[name] + 1,
      }
      try {
        await ref.update(document)
      } catch (e) {
        // TODO: error handling
      }
    },
    fetchReactions() {
      const ref = fireDb.collection('reactions').doc('gandhi')
      ref.onSnapshot(
        (docSnapshot) => {
          this.reactions = docSnapshot.data()
          AOS.refreshHard()
        },
        (err) => {
          // eslint-disable-next-line no-console
          console.log(err)
        }
      )
    },
  },
}
</script>
