<script>
import AOS from 'aos'
import { fireDb } from '@/plugins/firebase.js'
export default {
  filters: {
    formatNumber(num) {
      return Math.abs(num) > 999
        ? Math.sign(num) * (Math.abs(num) / 1000).toFixed(1) + 'k'
        : Math.sign(num) * Math.abs(num)
    },
  },
  data() {
    return {
      reactions: {
        blessed: 0,
        clap: 0,
        love: 0,
        superlike: 0,
        cool: 0,
        profileA: 0,
      },
      showArrow: false,
    }
  },
  mounted() {
    this.fetchReactions()
    const vm = this
    setTimeout(function () {
      vm.showArrow = true
    }, 1000)
  },
  methods: {
    async updateReaction(name) {
      const ref = fireDb.collection('reactions').doc('3vjaw54r0cwyXKBnbkbA')
      const document = {
        [name]: this.reactions[name] + 1,
      }
      try {
        await ref.update(document)
      } catch (e) {
        // TODO: error handling
      }
    },
    fetchReactions() {
      const ref = fireDb.collection('reactions').doc('3vjaw54r0cwyXKBnbkbA')
      ref.onSnapshot(
        (docSnapshot) => {
          this.reactions = docSnapshot.data()
          AOS.refreshHard()
        },
        (err) => {
          // eslint-disable-next-line no-console
          console.log(err)
        }
      )
    },
  },
}
</script>
